import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';

const QRScanner = () => {
  const [scanResult, setScanResult] = useState(null);
  const [scanError, setScanError] = useState(null);
  const [manualInput, setManualInput] = useState('');
  const [labName, setLabName] = useState('Main Lab');
  const [isScanning, setIsScanning] = useState(false);
  const [cameraError, setCameraError] = useState('');
  const [cameraActive, setCameraActive] = useState(false);
  
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  // Clean up on component unmount
  useEffect(() => {
    return () => {
      stopCameraScan();
    };
  }, []);

  const startCameraScan = async () => {
    try {
      setCameraError('');
      setScanError('');
      setScanResult(null);
      setIsScanning(true);
      setCameraActive(false);

      console.log('Requesting camera access...');

      // Get camera access - simplified approach
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 }
        } 
      });

      console.log('Camera access granted:', stream);
      
      streamRef.current = stream;
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.onloadedmetadata = () => {
          console.log('Video metadata loaded');
          videoRef.current.play().then(() => {
            console.log('Video playing');
            setCameraActive(true);
          }).catch(e => {
            console.error('Video play error:', e);
            setCameraError('Failed to start video: ' + e.message);
          });
        };
      }

    } catch (error) {
      console.error('Camera access error:', error);
      let errorMessage = `Camera Error: ${error.message}`;
      
      if (error.name === 'NotAllowedError') {
        errorMessage = 'Camera permission denied. Please allow camera access and refresh the page.';
      } else if (error.name === 'NotFoundError') {
        errorMessage = 'No camera found. Please check if camera is connected.';
      } else if (error.name === 'NotSupportedError') {
        errorMessage = 'Camera not supported in this browser. Try Chrome or Firefox.';
      }
      
      setCameraError(errorMessage);
      setIsScanning(false);
    }
  };

  const stopCameraScan = () => {
    if (streamRef.current) {
      console.log('Stopping camera stream');
      streamRef.current.getTracks().forEach(track => {
        track.stop();
        console.log('Stopped track:', track.kind);
      });
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setIsScanning(false);
    setCameraActive(false);
  };

  const testCamera = async () => {
    try {
      console.log('Testing camera access...');
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const tracks = stream.getTracks();
      console.log('Camera test successful. Tracks:', tracks.length);
      
      // Show track info
      tracks.forEach((track, index) => {
        console.log(`Track ${index}:`, track.kind, track.label);
      });
      
      // Clean up
      tracks.forEach(track => track.stop());
      
      alert(`✅ Camera test successful!\nFound ${tracks.length} camera track(s)`);
      return true;
    } catch (error) {
      console.error('Camera test failed:', error);
      alert(`❌ Camera test failed:\n${error.message}`);
      return false;
    }
  };

  const checkCameraPermissions = () => {
    // Check if browser supports permissions API
    if (navigator.permissions) {
      navigator.permissions.query({ name: 'camera' })
        .then(permissionStatus => {
          console.log('Camera permission state:', permissionStatus.state);
          alert(`Camera permission: ${permissionStatus.state}`);
        })
        .catch(error => {
          console.log('Permissions API not fully supported');
        });
    } else {
      alert('Permissions API not supported in this browser');
    }
  };

  const handleScan = async (qrContent) => {
    try {
      setScanError(null);
      
      // Validate QR content
      let qrData;
      try {
        qrData = JSON.parse(qrContent);
        if (!qrData.id || !qrData.name) {
          throw new Error('Invalid QR code format');
        }
      } catch (e) {
        setScanError('Invalid QR code format. Please make sure you scan a valid lab QR code.');
        return;
      }

      const response = await axios.post('http://localhost:5000/api/scan', {
        qr_content: qrContent,
        lab_name: labName
      });
      
      if (response.data.success) {
        setScanResult(response.data);
        stopCameraScan();
      } else {
        setScanError(response.data.message);
      }
    } catch (error) {
      setScanError('Error: ' + error.message);
    }
  };

  const handleManualScan = () => {
    if (manualInput.trim()) {
      handleScan(manualInput);
    } else {
      setScanError('Please enter QR code data');
    }
  };

  return (
    <div className="card">
      <h2><i className="fas fa-qrcode"></i> QR Scanner</h2>
      
      <div className="form-group">
        <label>Lab Name</label>
        <select className="form-control" value={labName} onChange={(e) => setLabName(e.target.value)}>
          <option value="Main Lab">Main Lab</option>
          <option value="Chemistry Lab">Chemistry Lab</option>
          <option value="Physics Lab">Physics Lab</option>
          <option value="Biology Lab">Biology Lab</option>
          <option value="Computer Lab">Computer Lab</option>
        </select>
      </div>

      {/* Camera Scanner Section */}
      <div className="scanner-section">
        <h3><i className="fas fa-camera"></i> Camera Scanner</h3>
        
        {cameraError && (
          <div className="scan-result scan-error">
            <h4>❌ Camera Issue</h4>
            <p>{cameraError}</p>
            <div style={{marginTop: '15px'}}>
              <button onClick={testCamera} className="btn btn-primary" style={{marginRight: '10px'}}>
                <i className="fas fa-video"></i> Test Camera
              </button>
              <button onClick={checkCameraPermissions} className="btn btn-primary">
                <i className="fas fa-shield-alt"></i> Check Permissions
              </button>
            </div>
          </div>
        )}

        <div style={{ display: 'flex', gap: '10px', marginBottom: '15px', flexWrap: 'wrap' }}>
          {!isScanning ? (
            <button onClick={startCameraScan} className="btn btn-primary">
              <i className="fas fa-camera"></i> Start Camera Scanner
            </button>
          ) : (
            <button onClick={stopCameraScan} className="btn btn-primary" style={{backgroundColor: '#f44336'}}>
              <i className="fas fa-stop"></i> Stop Camera
            </button>
          )}
          
          <button onClick={testCamera} className="btn btn-primary" style={{backgroundColor: '#ff9800'}}>
            <i className="fas fa-bug"></i> Test Camera
          </button>
        </div>
        
        {isScanning && (
          <div className="scanner-container">
            <video 
              ref={videoRef} 
              style={{ 
                width: '100%', 
                maxWidth: '400px', 
                border: '3px solid #1e88e5', 
                borderRadius: '10px',
                background: '#000',
                transform: 'scaleX(-1)' // Mirror effect
              }}
              playsInline
              muted
            />
            <div style={{textAlign: 'center', marginTop: '10px'}}>
              {cameraActive ? (
                <p style={{color: '#4caf50'}}>
                  <i className="fas fa-check-circle"></i> Camera Active - Use Manual Input Below
                </p>
              ) : (
                <p style={{color: '#ff9800'}}>
                  <i className="fas fa-sync fa-spin"></i> Starting camera...
                </p>
              )}
              <p style={{color: '#666', fontSize: '0.9rem'}}>
                Note: Camera shows preview. Use manual input for scanning.
              </p>
            </div>
          </div>
        )}

        {!isScanning && (
          <div style={{ 
            width: '100%', 
            maxWidth: '400px', 
            height: '300px', 
            border: '2px dashed #ccc', 
            borderRadius: '10px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            margin: '20px auto',
            background: '#f9f9f9'
          }}>
            <div style={{textAlign: 'center', color: '#666'}}>
              <i className="fas fa-camera" style={{fontSize: '48px', marginBottom: '10px'}}></i>
              <p>Camera preview will appear here</p>
              <p style={{fontSize: '0.8rem'}}>Click "Start Camera Scanner"</p>
            </div>
          </div>
        )}
      </div>

      {/* Manual Input Section */}
      <div className="manual-section">
        <h3><i className="fas fa-keyboard"></i> Manual Input (Recommended)</h3>
        <p style={{color: '#666', marginBottom: '15px'}}>
          Copy the QR code data from Dashboard and paste it below:
        </p>
        <div className="form-group">
          <label>QR Code Data</label>
          <textarea
            className="form-control"
            value={manualInput}
            onChange={(e) => setManualInput(e.target.value)}
            placeholder='{"id":1,"name":"John Doe","email":"john@example.com"}'
            rows="3"
          />
        </div>
        <button onClick={handleManualScan} className="btn btn-primary">
          <i className="fas fa-paper-plane"></i> Process QR Data
        </button>
      </div>

      {/* Scan Results */}
      {scanResult && (
        <div className="scan-result scan-success">
          <h3>✅ Scan Successful!</h3>
          <p><strong>Action:</strong> {scanResult.action.toUpperCase()}</p>
          <p><strong>Name:</strong> {scanResult.person.name}</p>
          <p><strong>Email:</strong> {scanResult.person.email}</p>
          <p><strong>Lab:</strong> {scanResult.lab_name}</p>
          <p><strong>Time:</strong> {scanResult.timestamp}</p>
        </div>
      )}

      {scanError && (
        <div className="scan-result scan-error">
          <h3>❌ Scan Failed</h3>
          <p>{scanError}</p>
        </div>
      )}
    </div>
  );
};

export default QRScanner;
