import React, { useState } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', phone: '', department: ''
  });
  const [qrData, setQrData] = useState(null);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const response = await axios.post('http://localhost:5000/api/register', formData);
      if (response.data.success) {
        setQrData({
          id: response.data.person_id,
          name: formData.name,
          email: formData.email
        });
        setMessage('Person registered successfully!');
        setFormData({ name: '', email: '', phone: '', department: '' });
      } else {
        setMessage(response.data.message);
      }
    } catch (error) {
      setMessage('Error: ' + error.message);
    }
  };

  return (
    <div>
      <div className="card">
        <h2><i className="fas fa-user-plus"></i> Register Person</h2>
        {message && <div className={`alert ${message.includes('successfully') ? 'alert-success' : 'alert-error'}`}>{message}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Full Name *</label>
            <input type="text" name="name" className="form-control" value={formData.name} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Email *</label>
            <input type="email" name="email" className="form-control" value={formData.email} onChange={handleChange} required />
          </div>
          <div className="form-group">
            <label>Phone</label>
            <input type="tel" name="phone" className="form-control" value={formData.phone} onChange={handleChange} />
          </div>
          <div className="form-group">
            <label>Department</label>
            <input type="text" name="department" className="form-control" value={formData.department} onChange={handleChange} />
          </div>
          <button type="submit" className="btn btn-primary">
            <i className="fas fa-save"></i> Register
          </button>
        </form>
      </div>

      {qrData && (
        <div className="card">
          <h2><i className="fas fa-qrcode"></i> QR Code Data</h2>
          <div className="qr-container">
            <div style={{ background: 'white', padding: '20px', display: 'inline-block', borderRadius: '10px' }}>
              <pre>{JSON.stringify(qrData, null, 2)}</pre>
            </div>
            <p style={{ marginTop: '15px' }}>Copy this data for scanning</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
