import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Html5QrcodeScanner } from 'html5-qrcode';

const QRScanner = () => {
  const [scanResult, setScanResult] = useState(null);
  const [scanError, setScanError] = useState(null);
  const [manualInput, setManualInput] = useState('');
  const [labName, setLabName] = useState('Main Lab');
  const [isScanning, setIsScanning] = useState(false);
  const [scanner, setScanner] = useState(null);
  const [cameraError, setCameraError] = useState('');

  useEffect(() => {
    return () => {
      if (scanner) {
        stopCameraScan();
      }
    };
  }, [scanner]);

  const startCameraScan = async () => {
    try {
      setCameraError('');
      setIsScanning(true);
      setScanResult(null);
      setScanError(null);

      const html5QrcodeScanner = new Html5QrcodeScanner(
        "qr-reader",
        { 
          fps: 10, 
          qrbox: { width: 250, height: 250 },
          aspectRatio: 1.0
        },
        false
      );

      await html5QrcodeScanner.render(
        (decodedText) => {
          handleScan(decodedText);
          stopCameraScan();
        },
        (error) => {
          console.log("Scan error:", error);
        }
      );

      setScanner(html5QrcodeScanner);
    } catch (error) {
      setCameraError('Camera access denied or not available. Please check permissions.');
      setIsScanning(false);
      console.error("Camera error:", error);
    }
  };

  const stopCameraScan = () => {
    if (scanner) {
      scanner.clear().catch(error => {
        console.log("Scanner cleanup error:", error);
      });
      setScanner(null);
    }
    setIsScanning(false);
  };

  const handleScan = async (qrContent) => {
    try {
      setScanError(null);
      const response = await axios.post('http://localhost:5000/api/scan', {
        qr_content: qrContent,
        lab_name: labName
      });
      if (response.data.success) {
        setScanResult(response.data);
        setManualInput('');
      } else {
        setScanError(response.data.message);
      }
    } catch (error) {
      setScanError('Error: ' + error.message);
    }
  };

  const handleManualScan = () => {
    if (manualInput.trim()) {
      handleScan(manualInput);
    } else {
      setScanError('Please enter QR code data');
    }
  };

  return (
    <div className="card">
      <h2><i className="fas fa-qrcode"></i> QR Scanner</h2>
      
      <div className="form-group">
        <label>Lab Name</label>
        <select className="form-control" value={labName} onChange={(e) => setLabName(e.target.value)}>
          <option value="Main Lab">Main Lab</option>
          <option value="Chemistry Lab">Chemistry Lab</option>
          <option value="Physics Lab">Physics Lab</option>
          <option value="Biology Lab">Biology Lab</option>
          <option value="Computer Lab">Computer Lab</option>
        </select>
      </div>

      {/* Webcam Scanner Section */}
      <div className="scanner-section">
        <h3><i className="fas fa-camera"></i> Webcam Scanner</h3>
        
        {cameraError && (
          <div className="scan-result scan-error">
            <p>{cameraError}</p>
            <p><strong>Tips:</strong></p>
            <ul style={{textAlign: 'left', margin: '10px 0'}}>
              <li>Allow camera permissions when prompted</li>
              <li>Make sure no other app is using the camera</li>
              <li>Try using Chrome or Firefox browser</li>
              <li>Ensure good lighting on the QR code</li>
            </ul>
          </div>
        )}

        {!isScanning ? (
          <button onClick={startCameraScan} className="btn btn-primary">
            <i className="fas fa-camera"></i> Start Camera Scanner
          </button>
        ) : (
          <button onClick={stopCameraScan} className="btn btn-primary" style={{backgroundColor: '#f44336'}}>
            <i className="fas fa-stop"></i> Stop Camera
          </button>
        )}
        
        {isScanning && (
          <div className="scanner-container">
            <div id="qr-reader" style={{width: '100%'}}></div>
            <p style={{textAlign: 'center', marginTop: '10px', color: '#666'}}>
              Point camera at QR code to scan. Make sure the QR code is well-lit.
            </p>
          </div>
        )}
      </div>

      {/* Manual Input Section */}
      <div className="manual-section">
        <h3><i className="fas fa-keyboard"></i> Manual Input</h3>
        <div className="form-group">
          <label>Or Enter QR Code Data Manually</label>
          <textarea
            className="form-control"
            value={manualInput}
            onChange={(e) => setManualInput(e.target.value)}
            placeholder='Paste QR code data here: {"id":1,"name":"John","email":"john@example.com"}'
            rows="3"
          />
        </div>
        <button onClick={handleManualScan} className="btn btn-primary">
          <i className="fas fa-paper-plane"></i> Process Manual Input
        </button>
      </div>

      {/* Scan Results */}
      {scanResult && (
        <div className="scan-result scan-success">
          <h3>✅ Scan Successful!</h3>
          <p><strong>Action:</strong> {scanResult.action.toUpperCase()}</p>
          <p><strong>Name:</strong> {scanResult.person.name}</p>
          <p><strong>Email:</strong> {scanResult.person.email}</p>
          <p><strong>Lab:</strong> {scanResult.lab_name}</p>
          <p><strong>Time:</strong> {scanResult.timestamp}</p>
        </div>
      )}

      {scanError && (
        <div className="scan-result scan-error">
          <h3>❌ Scan Failed</h3>
          <p>{scanError}</p>
        </div>
      )}
    </div>
  );
};

export default QRScanner;
