import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import jsQR from 'jsqr';

const QRScanner = () => {
  const [scanResult, setScanResult] = useState(null);
  const [scanError, setScanError] = useState(null);
  const [manualInput, setManualInput] = useState('');
  const [labName, setLabName] = useState('Main Lab');
  const [isScanning, setIsScanning] = useState(false);
  const [cameraError, setCameraError] = useState('');
  const [lastScanned, setLastScanned] = useState('');
  
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const streamRef = useRef(null);
  const scanIntervalRef = useRef(null);

  useEffect(() => {
    return () => {
      stopCameraScan();
    };
  }, []);

  const startCameraScan = async () => {
    try {
      setCameraError('');
      setScanError('');
      setScanResult(null);
      setIsScanning(true);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: "environment",
          width: { ideal: 1280 },
          height: { ideal: 720 }
        } 
      });

      streamRef.current = stream;
      
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        videoRef.current.setAttribute("playsinline", true);
        
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play().then(() => {
            startQRDetection();
          });
        };
      }

    } catch (error) {
      console.error('Camera error:', error);
      setCameraError(`Camera Error: ${error.message}`);
      setIsScanning(false);
    }
  };

  const startQRDetection = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');

    const scanQR = () => {
      if (video.readyState === video.HAVE_ENOUGH_DATA) {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        const imageData = context.getImageData(0, 0, canvas.width, canvas.height);
        const code = jsQR(imageData.data, imageData.width, imageData.height);
        
        if (code) {
          console.log('QR Code detected:', code.data);
          handleScan(code.data);
        }
      }
    };

    scanIntervalRef.current = setInterval(scanQR, 500); // Scan every 500ms
  };

  const stopCameraScan = () => {
    if (scanIntervalRef.current) {
      clearInterval(scanIntervalRef.current);
    }
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
    setIsScanning(false);
  };

  const handleScan = async (qrContent) => {
    // Prevent scanning the same code multiple times
    if (qrContent === lastScanned) {
      return;
    }
    setLastScanned(qrContent);
    
    try {
      setScanError(null);
      
      console.log('Processing QR content:', qrContent);
      
      // Parse and validate QR content
      let qrData;
      try {
        qrData = JSON.parse(qrContent);
        console.log('Parsed QR data:', qrData);
        
        if (!qrData.id || !qrData.name || !qrData.email) {
          throw new Error('Missing required fields in QR code');
        }
      } catch (e) {
        setScanError(`Invalid QR code format: ${e.message}. Make sure you're scanning a valid lab QR code.`);
        return;
      }

      const response = await axios.post('http://localhost:5000/api/scan', {
        qr_content: qrContent,
        lab_name: labName
      });
      
      console.log('Server response:', response.data);
      
      if (response.data.success) {
        setScanResult(response.data);
        // Don't stop camera - allow continuous scanning
      } else {
        setScanError(response.data.message);
      }
    } catch (error) {
      console.error('Scan error:', error);
      setScanError('Error scanning QR code: ' + error.message);
    }
  };

  const handleManualScan = () => {
    if (manualInput.trim()) {
      handleScan(manualInput);
      setManualInput(''); // Clear input after scanning
    } else {
      setScanError('Please enter QR code data');
    }
  };

  const testScan = () => {
    // Test with sample data
    const testData = '{"id":1,"name":"Test User","email":"test@example.com"}';
    handleScan(testData);
  };

  return (
    <div className="card">
      <h2><i className="fas fa-qrcode"></i> QR Scanner</h2>
      
      <div className="form-group">
        <label>Lab Name</label>
        <select className="form-control" value={labName} onChange={(e) => setLabName(e.target.value)}>
          <option value="Main Lab">Main Lab</option>
          <option value="Chemistry Lab">Chemistry Lab</option>
          <option value="Physics Lab">Physics Lab</option>
          <option value="Biology Lab">Biology Lab</option>
          <option value="Computer Lab">Computer Lab</option>
        </select>
      </div>

      {/* Camera Scanner Section */}
      <div className="scanner-section">
        <h3><i className="fas fa-camera"></i> Camera Scanner</h3>
        
        {cameraError && (
          <div className="scan-result scan-error">
            <h4>❌ Camera Issue</h4>
            <p>{cameraError}</p>
          </div>
        )}

        <div style={{ display: 'flex', gap: '10px', marginBottom: '15px', flexWrap: 'wrap' }}>
          {!isScanning ? (
            <button onClick={startCameraScan} className="btn btn-primary">
              <i className="fas fa-camera"></i> Start Camera Scanner
            </button>
          ) : (
            <button onClick={stopCameraScan} className="btn btn-primary" style={{backgroundColor: '#f44336'}}>
              <i className="fas fa-stop"></i> Stop Camera
            </button>
          )}
          
          <button onClick={testScan} className="btn btn-primary" style={{backgroundColor: '#ff9800'}}>
            <i className="fas fa-vial"></i> Test Scan
          </button>
        </div>
        
        {isScanning && (
          <div className="scanner-container">
            <video 
              ref={videoRef} 
              style={{ 
                width: '100%', 
                maxWidth: '400px', 
                border: '3px solid #1e88e5', 
                borderRadius: '10px',
                background: '#000'
              }}
              playsInline
              muted
            />
            <canvas ref={canvasRef} style={{ display: 'none' }} />
            <p style={{textAlign: 'center', marginTop: '10px', color: '#4caf50'}}>
              <i className="fas fa-search"></i> Scanning for QR codes... Point camera at QR code
            </p>
          </div>
        )}
      </div>

      {/* Manual Input Section */}
      <div className="manual-section">
        <h3><i className="fas fa-keyboard"></i> Manual Input</h3>
        <p style={{color: '#666', marginBottom: '15px'}}>
          Copy QR data from Dashboard and paste below:
        </p>
        <div className="form-group">
          <label>QR Code Data</label>
          <textarea
            className="form-control"
            value={manualInput}
            onChange={(e) => setManualInput(e.target.value)}
            placeholder='{"id":1,"name":"John Doe","email":"john@example.com"}'
            rows="3"
          />
        </div>
        <button onClick={handleManualScan} className="btn btn-primary">
          <i className="fas fa-paper-plane"></i> Process QR Data
        </button>
      </div>

      {/* Scan Results */}
      {scanResult && (
        <div className="scan-result scan-success">
          <h3>✅ Scan Successful!</h3>
          <p><strong>Action:</strong> {scanResult.action.toUpperCase()}</p>
          <p><strong>Name:</strong> {scanResult.person.name}</p>
          <p><strong>Email:</strong> {scanResult.person.email}</p>
          <p><strong>Lab:</strong> {scanResult.lab_name}</p>
          <p><strong>Time:</strong> {scanResult.timestamp}</p>
          <button 
            onClick={() => setScanResult(null)} 
            className="btn btn-primary"
            style={{marginTop: '10px'}}
          >
            Scan Another
          </button>
        </div>
      )}

      {scanError && (
        <div className="scan-result scan-error">
          <h3>❌ Scan Failed</h3>
          <p>{scanError}</p>
          <button 
            onClick={() => setScanError(null)} 
            className="btn btn-primary"
            style={{marginTop: '10px'}}
          >
            Try Again
          </button>
        </div>
      )}
    </div>
  );
};

export default QRScanner;
